<template>
	<view class="nav-back" :style="{top: barHeight + 'px'}">
		<view class="back-btn" @click="backBtn()">
			<text class="hxicon hx-navbar__icon" >&#xe679;</text>
		</view>
	</view>
</template>

<script>
	export default {
		data() {
			return {
				barHeight:0
			}
		},
		mounted() {
			// #ifdef MP-WEIXIN
			if(uni.getSystemInfoSync().statusBarHeight > 20) {
				this.barHeight = uni.getSystemInfoSync().statusBarHeight - 20
			}
			// #endif
		},
		methods: {
			backBtn() {
			    var cur = getCurrentPages();
			    if (cur.length > 1) {
			        uni.navigateBack({
			            delta: 1
			        });
			    } else {
			        uni.reLaunch({
			            url: '/pages/index/index'
			        });
			    }
			}
		}
	}
</script>

